      padding: 20px;
      border-radius: 10px;
      overflow-y: scroll;
    }
    .input-area {
      display: flex;
      width: 60%;
      margin: 0 auto 20px;
    }
    #userInput {
      flex: 1;
      padding: 10px;
      border: none;
      border-top-left-radius: 5px;
      border-bottom-left-radius: 5px;
    }
    #sendBtn {
      background: #3a82f7;
      color: white;
      border: none;
      padding: 10px 20px;
      border-top-right-radius: 5px;
      border-bottom-right-radius: 5px;
    }
  </style>
</head>
<body>
  <h2 style="text-align:center;">🤖 AI Assistant</h2>
  <div class="chat-box" id="chatBox"></div>

  <div class="input-area">
    <input type="text" id="userInput" placeholder="Ask me something..." />
    <button id="sendBtn" onclick="sendMsg()">Send</button>
  </div>

  <script>
    function sendMsg() {
      let input = document.querySelector("#userInput").value;
      if (input == "") return;

      let chat = document.getElementById("chatBox");
      chat.innerHTML += `<p><b>You:</b> ${input}</p>`;
      setTimeout(() => {
        chat.innerHTML += `<p><b>AI:</b> ${generateResponse(input)}</p>`;
      }, 600);
      document.querySelector("#userInput").value = "";
    }

    function generateResponse(text) {
      if (text.includes("hi")) return "Hey there human!";
      else if (text.includes("weather"))
        return "It looks sunny... maybe 🌤️, I don’t have sensors tho!";
      else return "Interesting... tell me more.";
    }
  </script>
</body>
</html>
